#include <cstdio>
#include <cstring>
#include <iostream>
#include <sys/socket.h> // 核心 socket 函数
#include <arpa/inet.h>  // IP 地址转换 struct sockaddr_in, inet_addr
#include <unistd.h>     // 提供 close() 函数关闭文件描述符

int main() {
    // 1. 创建 socket (买个手机)
    // AF_INET: 使用 IPv4 地址
    // SOCK_STREAM: 使用 TCP 协议 (流式传输，可靠，有序)
    // 0: 自动选择协议 (通常就是 TCP)
    // 返回值 sockfd 是一个文件描述符 (int)，如果失败返回 -1
    int sockfd = socket(AF_INET, SOCK_STREAM, 0);
    if (sockfd == -1) {
        perror("socket create error");
        return -1;
    }

    // 2. 绑定 IP 和端口 (插上电话线，固定号码)
    struct sockaddr_in serv_addr;
    // 初始化结构体，防止垃圾数据
    std::memset(&serv_addr, 0, sizeof(serv_addr));

    serv_addr.sin_family = AF_INET; // IPv4
    // inet_addr 将字符串 IP 转换为网络字节序的整数
    serv_addr.sin_addr.s_addr = inet_addr("127.0.0.1"); 
    // htons (Host to Network Short): 将主机字节序(小端)转为网络字节序(大端)
    // 8888 是我们要监听的端口号
    serv_addr.sin_port = htons(8888); 

    // bind 将 sockfd 与上面的地址结构体绑定
    if (bind(sockfd, (struct sockaddr*)&serv_addr, sizeof(serv_addr)) == -1) {
        perror("bind error");
        return -1;
    }

    // 3. 监听 (设置手机为铃声模式，准备接客)
    // SOMAXCONN: 操作系统允许的最大连接队列长度
    if (listen(sockfd, SOMAXCONN) == -1) {
        perror("listen error");
        return -1;
    }

    std::cout << "Server is listening on 127.0.0.1:8888..." << std::endl;

    // 4. 接受连接 (电话响了，拿起来)
    struct sockaddr_in clnt_addr; // 用来存客户端的地址信息
    socklen_t clnt_addr_len = sizeof(clnt_addr);
    std::memset(&clnt_addr, 0, sizeof(clnt_addr));

    // accept 是阻塞的！程序运行到这里会停住，直到有客户端连上来
    // 它会返回一个新的 fd (clnt_sockfd)，专门用于和这个特定的客户端通信
    // 原来的 sockfd 继续监听其他新连接
    int clnt_sockfd = accept(sockfd, (struct sockaddr*)&clnt_addr, &clnt_addr_len);
    if (clnt_sockfd == -1) {
        perror("accept error");
        return -1;
    }

    // 打印是谁连上了
    std::cout << "New client connected: " 
              << inet_ntoa(clnt_addr.sin_addr) // 将网络整数转回 IP 字符串
              << ":" << ntohs(clnt_addr.sin_port) << std::endl;

    // 5. 通信结束，关闭连接
    // 在 Linux 中，socket 也是文件，用 close 销毁
    close(clnt_sockfd);
    close(sockfd);

    return 0;
}
